import React, { useState } from 'react';
import './StorePage.css'; // Importa el archivo de estilos CSS

function StorePage() {
  const [products, setProducts] = useState([
    { id: 1, name: 'Producto 1', price: 10.99 },
    { id: 2, name: 'Producto 2', price: 19.99 },
    { id: 3, name: 'Producto 3', price: 5.99 },
    // Agrega más productos según sea necesario
  ]);

  // Función para agregar un producto al carrito
  const addToCart = (productId) => {
    // Lógica para agregar el producto al carrito
    // Puedes implementar esta función según tus necesidades
    console.log(`Producto agregado al carrito: ${productId}`);
  };

  return (
    <div className="store-page">
      <h1 className="store-page-title">Tienda</h1>

      <div className="sidebar">
        <h2>Categorías</h2>
        {/* Agrega aquí tu código para mostrar las categorías */}
      </div>

      <div className="product-list">
        {products.map((product) => (
          <div key={product.id} className="product-card">
            <h3>{product.name}</h3>
            <p>Precio: ${product.price}</p>
            <button onClick={() => addToCart(product.id)}>Agregar al carrito</button>
          </div>
        ))}
      </div>
    </div>
  );
}

export default StorePage;



