import React, { useState } from 'react';
import './StorePage.css'; // Importa el archivo de estilos CSS

function StorePage() {
  const [categories, setCategories] = useState([
    'Frutas',
    'Verduras',
    'Cereales',
    'Hortalizas',
    // Agrega más categorías según sea necesario
  ]);

  const [products, setProducts] = useState([
    {
      id: 1,
      name: 'Producto 1',
      price: 10.99,
      image: 'https://s2.ppllstatics.com/diariovasco/www/multimedia/202106/04/media/cortadas/platano-kUyC-RCIEbjdcaFn9Yc7KKpofzYN-1248x770@Diario%20Vasco-DiarioVasco.jpg',
      description: 'Descripción del producto 1',
    },
    {
      id: 2,
      name: 'Producto 2',
      price: 19.99,
      image: 'https://example.com/product2.jpg',
      description: 'Descripción del producto 2',
    },
    {
      id: 3,
      name: 'Producto 3',
      price: 5.99,
      image: 'https://example.com/product3.jpg',
      description: 'Descripción del producto 3',
    },
    {
      id: 4,
      name: 'Producto 4',
      price: 6.99,
      image: 'https://example.com/product3.jpg',
      description: 'Descripción del producto 4',
    },
    {
      id: 5,
      name: 'Producto 5',
      price: 6.99,
      image: 'https://example.com/product3.jpg',
      description: 'Descripción del producto 5',
    },
    {
      id: 6,
      name: 'Producto 6',
      price: 6.99,
      image: 'data:image/jpeg;base64,',
      description: 'Descripción del producto 6',
    },
    {
      id: 7,
      name: 'Producto 7',
      price: 19.99,
      image: 'https://example.com/product7.jpg',
      description: 'Descripción del producto 7',
    },
    {
      id: 8,
      name: 'Producto 8',
      price: 5.99,
      image: 'https://example.com/product3.jpg',
      description: 'Descripción del producto 8',
    },
    {
      id: 9,
      name: 'Producto 9',
      price: 6.99,
      image: 'https://example.com/product3.jpg',
      description: 'Descripción del producto 9',
    },
    {
      id: 10,
      name: 'Producto 10',
      price: 6.99,
      image: 'https://example.com/product3.jpg',
      description: 'Descripción del producto 10',
    },
    {
      id: 11,
      name: 'Producto 11',
      price: 6.99,
      image: 'https://example.com/product3.jpg',
      description: 'Descripción del producto 11',
    },
    // Agrega más productos según sea necesario
  ]);

  const [sortOption, setSortOption] = useState('');

  const addToCart = (productId) => {
    console.log(`Producto agregado al carrito: ${productId}`);
  };

  const showProductDetails = (productId) => {
    const product = products.find((p) => p.id === productId);
    if (product) {
      console.log(`Detalles del producto ${productId}:`, product);
    }
  };

  const handleSortChange = (event) => {
    setSortOption(event.target.value);
  };

  const sortedProducts = [...products].sort((a, b) => {
    if (sortOption === 'name') {
      return a.name.localeCompare(b.name);
    } else if (sortOption === 'price') {
      return a.price - b.price;
    } else {
      return 0;
    }
  });

  return (
    <div className="store-page">
      <div className="sidebar">
        <h2>Categorías</h2>
        <ul>
          {categories.map((category) => (
            <li key={category}>{category}</li>
          ))}
        </ul>
      </div>

      <div className="product-list">
        <div className="product-list-header">
          <h3>Productos</h3>
          <select value={sortOption} onChange={handleSortChange}>
            <option value="">Ordenar por</option>
            <option value="name">Nombre</option>
            <option value="price">Precio</option>
          </select>
        </div>

        {sortedProducts.map((product) => (
          <div key={product.id} className="product-card">
            <img src={product.image} alt={product.name} className="product-image" />
            <h3>{product.name}</h3>
            <p>Precio: ${product.price}</p>
            <div className="product-actions">
              <button onClick={() => addToCart(product.id)}>Agregar al carrito</button>
              <span className="product-info-icon" onClick={() => showProductDetails(product.id)}>
                ℹ️
              </span>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default StorePage;




