import React, { useEffect,useState } from 'react';
import './StorePage.css'; // Importa el archivo de estilos CSS
import Axios from 'axios';

function StorePage() {
  const [categories, setCategories] = useState([
    'Frutas',
    'Verduras',
    'Cereales',
    'Hortalizas',
    // Agrega más categorías según sea necesario
  ]);

  /*const [products, setProducts] = useState([
    {
      id: 1,
      name: 'Producto 1',
      price: 10.99,
      image: 'https://s2.ppllstatics.com/diariovasco/www/multimedia/202106/04/media/cortadas/platano-kUyC-RCIEbjdcaFn9Yc7KKpofzYN-1248x770@Diario%20Vasco-DiarioVasco.jpg',
      description: 'Descripción del producto 1',
    },
    {
      id: 2,
      name: 'Producto 2',
      price: 19.99,
      image: 'https://example.com/product2.jpg',
      description: 'Descripción del producto 2',
    },
    {
      id: 3,
      name: 'Producto 3',
      price: 5.99,
      image: 'https://example.com/product3.jpg',
      description: 'Descripción del producto 3',
    },
    {
      id: 4,
      name: 'Producto 4',
      price: 6.99,
      image: 'https://example.com/product3.jpg',
      description: 'Descripción del producto 4',
    },
    {
      id: 5,
      name: 'Producto 5',
      price: 6.99,
      image: 'https://example.com/product3.jpg',
      description: 'Descripción del producto 5',
    },
    {
      id: 6,
      name: 'Producto 6',
      price: 6.99,
      image: 'https://www.recetasnestlecam.com/sites/default/files/2022-04/tipos-de-manzana-royal-gala.jpg',
      description: 'Descripción del producto 6',
    },
    {
      id: 7,
      name: 'Producto 7',
      price: 19.99,
      image: 'https://example.com/product7.jpg',
      description: 'Descripción del producto 7',
    },
    {
      id: 8,
      name: 'Producto 8',
      price: 5.99,
      image: 'https://example.com/product3.jpg',
      description: 'Descripción del producto 8',
    },
    {
      id: 9,
      name: 'Producto 9',
      price: 6.99,
      image: 'https://example.com/product3.jpg',
      description: 'Descripción del producto 9',
    },
    {
      id: 10,
      name: 'Producto 10',
      price: 6.99,
      image: 'https://example.com/product3.jpg',
      description: 'Descripción del producto 10',
    },
    {
      id: 11,
      name: 'Producto 10',
      price: 6.99,
      image: 'https://example.com/product3.jpg',
      description: 'Descripción del producto 10',
    },
    // Agrega más productos según sea necesario
  ]);*/

  const [sortOption, setSortOption] = useState('');
  const [searchTerm, setSearchTerm] = useState('');
  const [currentPage, setCurrentPage] = useState(1);
  const productsPerPage = 10;
  
  const [products,setProducts] = useState([])

  useEffect(()=>{
    Axios.get("http://localhost:5000/fb/producto/get")
    .then(res=>{setProducts(res.data)
    console.log("Los productos se han guardado.\n",products)})
    .catch(err=>console.log(err,"\nNo se han podido cargar los datos."))
  },[])

  const addToCart = (productId) => {
    console.log(`Producto agregado al carrito: ${productId}`);
  };

  const showProductDetails = (productId) => {
    const product = products.find((p) => p.id === productId);
    if (product) {
      console.log(`Detalles del producto ${productId}:`, product);
    }
  };

  const handleSortChange = (event) => {
    setSortOption(event.target.value);
  };

  const handleSearch = (event) => {
    setSearchTerm(event.target.value);
  };

  const handlePageChange = (pageNumber) => {
    setCurrentPage(pageNumber);
  };

  const sortedProducts = [...products].sort((a, b) => {
    if (sortOption === 'name') {
      return a.pro_nombre.localeCompare(b.pro_nombre);
    } else if (sortOption === 'price') {
      return a.pro_precio - b.pro_precio;
    } else {
      return 0;
    }
  });

  const filteredProducts = sortedProducts.filter((product) =>
    product.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const indexOfLastProduct = currentPage * productsPerPage;
  const indexOfFirstProduct = indexOfLastProduct - productsPerPage;
  const currentProducts = filteredProducts.slice(indexOfFirstProduct, indexOfLastProduct);

  const totalPages = Math.ceil(filteredProducts.length / productsPerPage);

  return (
    <div className="store-page">
      <div className='filter-container'>
        <div className="search-bar-products">
          <input
            type="text"
            placeholder="Buscar productos"
            value={searchTerm}
            onChange={handleSearch}
          />
        </div>
        <div>
          <select value={sortOption} onChange={handleSortChange}>
                <option value="">Ordenar por</option>
                <option value="name">Nombre</option>
                <option value="price">Precio</option>
          </select>
        </div>
      </div>
      <div className="product-list">
        <div className="product-list-header">
          <div className="sidebar">
            <h2>Categorías</h2>
            <ul>
              {categories.map((category) => (
                <li key={category}>{category}</li>
              ))}
            </ul>
          </div>
        </div>
        <div className='products-container'>
          {currentProducts.map((product) => (
            <div key={product.id} className="product-card">
              <img src={product.pro_imagen} alt={product.pro_nombre} className="product-image" />
              <h3>{product.pro_nombre}</h3>
              <p>Precio: ${product.pro_precio}</p>
              <div className="product-actions">
                <button onClick={() => addToCart(product.id)}>Agregar al carrito</button>
                <span className="product-info-icon" onClick={() => showProductDetails(product.id)}>
                  ℹ️
                </span>
              </div>
            </div>
          ))}
        </div>
      </div>
      <div className="product-list-footer">
          {Array.from({ length: totalPages }, (_, index) => index + 1).map((pageNumber) => (
            <span
              key={pageNumber}
              className={`page-number ${pageNumber === currentPage ? 'active' : ''}`}
              onClick={() => handlePageChange(pageNumber)}
            >
              {pageNumber}
            </span>
          ))}
        </div>
    </div>
  );
}

export default StorePage;



