import React, { useState } from 'react';
import './Register.css'; // Importa el archivo de estilos CSS

const provinces = [
  { "name": "Azuay", "cities": ["Cuenca", "Gualaceo", "Chordeleg", "Paute" , "Sigsig"] },
  { "name": "Bolívar", "cities": ["Guaranda" ,"San Miguel", "Chillanes", "Echeandía", "Caluma"] },
  { "name": "Cañar", "cities": ["Azogues", "Biblián", "La Troncal", "El Tambo", "Déleg"] },
  { "name": "Carchi", "cities": ["Tulcán" ,"San Gabriel","Mira", "Espejo","Montúfar"] },
  { "name": "Chimborazo", "cities": ["Riobamba","Guano","Chambo","Alausí","Colta"] },
  { "name": "Cotopaxi", "cities": ["Latacunga","Salcedo","La Maná","Pujilí","Saquisilí"] },
  { "name": "El Oro", "cities": ["Machala", "Santa Rosa", "Pasaje" , "Huaquillas" , "Zaruma"] },
  { "name": "Esmeraldas", "cities": ["Esmeraldas","Atacames","Rioverde","Muisne","Quinindé"] },
  { "name": "Galápagos", "cities": ["Puerto Ayora (isla Santa Cruz)","Puerto Baquerizo Moreno (isla San Cristóbal)","Isabela","Floreana","Baltra"] },
  { "name": "Guayas", "cities": ["Guayaquil","Daule","Durán","Samborondón","Milagro"] },
  { "name": "Imbabura", "cities": ["Ibarra","Otavalo","Cotacachi","Atuntaqui","Pimampiro"] },
  { "name": "Loja", "cities": ["Loja","Zamora","Catamayo","Macará","Pindal"] },
  { "name": "Los Ríos", "cities": ["Babahoyo","Quevedo","Vinces","Ventanas","Baba"] },
  { "name": "Manabí", "cities": ["Portoviejo","Manta","Bahía de Caráquez","Jipijapa","Chone"] },
  { "name": "Morona Santiago", "cities": ["Macas","Sucúa","Huamboya","Logroño","Taisha"] },
  { "name": "Napo", "cities": ["Tena","El Chaco","Archidona","Baeza","Cuyabeno"] },
  { "name": "Orellana", "cities": ["El Coca","La Joya de los Sachas","Francisco de Orellana","Loreto","Aguarico"] },
  { "name": "Pastaza", "cities": ["Puyo","Santa Clara","Mera","Shell","Arajuno"] },
  { "name": "Pichincha", "cities": ["Quito","Cayambe","Machachi","Sangolquí","Pedro Moncayo"] },
  { "name": "Santa Elena", "cities": ["Salinas","La Libertad","Santa Elena","Ballenita","Ancón"] },
  { "name": "Santo Domingo de los Tsáchilas", "cities": ["Santo Domingo","La Concordia","Quevedo","El Carmen","Puerto Quito"] },
  { "name": "Sucumbíos", "cities": ["Nueva Loja (Lago Agrio)","Shushufindi","Lumbaquí","Gonzalo Pizarro","Cuyabeno"] },
  { "name": "Tungurahua", "cities": ["Ambato","Pelileo","Cevallos","Baños","Patate"] },
  { "name": "Zamora Chinchipe", "cities": ["Zamora","Yantzaza","Centinela del Cóndor","Nangaritza","El Pangui"] }
];

function Register() {
  const [name, setName] = useState('');
  const [userType, setUserType] = useState('');
  const [nPhone, setNPhone] = useState('');
  const [password, setPassword] = useState('');
  const [photo, setPhoto] = useState(null);
  const [province, setProvince] = useState('');
  const [city, setCity] = useState('');
  const [mainStreet, setMainStreet] = useState('');
  const [secondaryStreet, setSecondaryStreet] = useState('');
  const [postalCode, setPostalCode] = useState('');

  const handleRegister = (e) => {
    e.preventDefault();
    console.log('Registro de usuario:', name, userType, nPhone, password, photo, province, city, mainStreet, secondaryStreet, postalCode);
  };

  const handlePhotoUpload = (e) => {
    const file = e.target.files[0];
    setPhoto(URL.createObjectURL(file));
  };

  const handleProvinceChange = (e) => {
    const selectedProvince = e.target.value;
    setProvince(selectedProvince);
    setCity('');
  };

  const getCityOptions = () => {
    const selectedProvinceObj = provinces.find((p) => p.name === province);
    if (selectedProvinceObj) {
      return selectedProvinceObj.cities.map((c) => (
        <option key={c} value={c}>
          {c}
        </option>
      ));
    }
    return null;
  };

  return (
    <div className="register-container">
      <h2 className="register-title">Registro de usuarios</h2>
      <form onSubmit={handleRegister}>
        <input
          className="register-input"
          type="text"
          placeholder="Nombres"
          value={name}
          onChange={(e) => setName(e.target.value)}
          required
        />
        <select
          className="register-inputS"
          value={userType}
          onChange={(e) => setUserType(e.target.value)}
          required
        >
          <option value="">Seleccionar tipo de usuario</option>
          <option value="Vendedor">Vendedor</option>
          <option value="Cliente">Cliente</option>
        </select>
        <input
          className="register-input"
          type="text"
          placeholder="Número de teléfono"
          value={nPhone}
          onChange={(e) => setNPhone(e.target.value)}
          required
        />
        <input
          className="register-input"
          type="password"
          placeholder="Contraseña"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        <input
          className="register-input"
          type="file"
          accept="image/*"
          onChange={handlePhotoUpload}
        />
        {photo && <img src={photo} alt="Foto de perfil" />}
        <h3 className="register-subtitle">Dirección</h3>
        <select
          className="register-inputS"
          value={province}
          onChange={handleProvinceChange}
          required
        >
          <option value="">Seleccionar provincia</option>
          {provinces.map((p) => (
            <option key={p.name} value={p.name}>
              {p.name}
            </option>
          ))}
        </select>
        <select
          className="register-inputS"
          value={city}
          onChange={(e) => setCity(e.target.value)}
          required
        >
          <option value="">Seleccionar ciudad</option>
          {getCityOptions()}
        </select>
        <input
          className="register-input"
          type="text"
          placeholder="Calle Principal"
          value={mainStreet}
          onChange={(e) => setMainStreet(e.target.value)}
          required
        />
        <input
          className="register-input"
          type="text"
          placeholder="Calle Secundaria"
          value={secondaryStreet}
          onChange={(e) => setSecondaryStreet(e.target.value)}
          required
        />
        <input
          className="register-input"
          type="text"
          placeholder="Código Postal"
          value={postalCode}
          onChange={(e) => setPostalCode(e.target.value)}
          required
        />
        <button className="register-button" type="submit">Registrarse</button>
      </form>
    </div>
  );
}

export default Register;
