import React, { useState } from 'react';
import './Register.css'; // Importa el archivo de estilos CSS

function Register() {
  const [name, setName] = useState('');
  const [address, setAddress] = useState('');
  const [userType, setUserType] = useState('');
  const [nPhone, setNPhone] = useState('');
  const [password, setPassword] = useState('');

  const handleRegister = (e) => {
    e.preventDefault();
    console.log('Registro de usuario:', name, address, userType, nPhone, password);
  };

  return (
    <div className="register-container">
      <h2 className="register-title">Registro de usuarios</h2>
      <form onSubmit={handleRegister}>
        <input
          className="register-input"
          type="text"
          placeholder="Nombres"
          value={name}
          onChange={(e) => setName(e.target.value)}
          required
        />
        <input
          className="register-input"
          type="text"
          placeholder="Dirección"
          value={address}
          onChange={(e) => setAddress(e.target.value)}
          required
        />
        <input
          className="register-input"
          type="text"
          placeholder="Número de teléfono"
          value={nPhone}
          onChange={(e) => setNPhone(e.target.value)}
          required
        />
        <input
          className="register-input"
          type="password"
          placeholder="Contraseña"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
         <select
          className="register-input"
          value={userType}
          onChange={(e) => setUserType(e.target.value)}
          required
        >
          <option value="">Seleccionar tipo de usuario</option>
          <option value="Vendedor">Vendedor</option>
          <option value="Cliente">Cliente</option>
        </select>
        <button className="register-button" type="submit">Registrarse</button>
      </form>
    </div>
  );
}

export default Register;
