import React, { useState, useEffect } from 'react';
import './ModalEditProduct.css';

const [categories, setCategories] = useState([]); // Estado para almacenar las categorías


const ModalEditProduct = ({ isOpen, onClose, productToEdit, handleEdit, categories }) => {
  const initialProduct = productToEdit ? productToEdit : {
    pro_nombre: '',
    pro_precio: '',
    pro_stock: '',
    pro_descripcion: '',
    pro_categoria: '',
  };

  const [editedProduct, setEditedProduct] = useState(initialProduct);

  useEffect(() => {
    // Cuando productToEdit cambia, actualiza el estado del modal
    if (productToEdit) {
      setEditedProduct(productToEdit);
    }
  }, [productToEdit]);

  useEffect(() => {
    // Llama a la API para obtener las categorías
    fetch(`http://localhost:5000/fb/categoria/get`)
      .then((response) => {
        if (!response.ok) {
          throw new Error('Error en la solicitud al servidor');
        }
        return response.json();
      })
      .then((data) => {
        setCategories(data);
      })
      .catch((error) => console.error('Error al cargar las categorías', error));
  }, []); // Esto asegura que se carguen las categorías solo una vez al montar el componente
  

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setEditedProduct({
      ...editedProduct,
      [name]: value,
    });
  };

  const onSave = (e) => {
    e.preventDefault();
    handleEdit(editedProduct);
    onClose();
  };

  if (!isOpen) return null;

  return (
    <div className="modal-overlay">
      <div className="modal-content-product">
        <div className='form-container'>
          <form className='modal-form' onSubmit={onSave}>
            <div className="form-group-pair">
              <div>
                <label htmlFor="pro_nombre">Nombre</label>
                <input
                  type="text"
                  className="form-control"
                  name="pro_nombre"
                  value={editedProduct.pro_nombre}
                  onChange={handleInputChange}
                />
              </div>
              <div>
                <label htmlFor="pro_precio">Precio</label>
                <input
                  type="number"
                  className="form-control"
                  name="pro_precio"
                  value={editedProduct.pro_precio}
                  onChange={handleInputChange}
                />
              </div>
            </div>
            <div className="form-group-pair">
              <div>
                <label htmlFor="pro_stock">Stock</label>
                <input
                  type="number"
                  className="form-control"
                  name="pro_stock"
                  value={editedProduct.pro_stock}
                  onChange={handleInputChange}
                />
              </div>
              <div>
                <label htmlFor="pro_descripcion">Descripción</label>
                <textarea
                  className="form-control"
                  name="pro_descripcion"
                  value={editedProduct.pro_descripcion}
                  onChange={handleInputChange}
                />
              </div>
            </div>
            <div className='btn-add-container'>
              <label htmlFor="pro_categoria">Categoría:</label>
              <select
                id="pro_categoria"
                name="pro_categoria"
                value={editedProduct.pro_categoria}
                onChange={handleInputChange}
              >
                {categories.map((category) => (
                  <option key={category.id} value={category.cat_nombre}>
                    {category.cat_nombre}
                  </option>
                ))}
              </select>
            </div>
            <div className="form-group">
              <button type="submit" className="btn-save">Guardar</button>
            </div>
          </form>
        </div>
        <span className="modal-close-product" onClick={onClose}>
          &times;
        </span>
      </div>
    </div>
  );
};

export default ModalEditProduct;
