import React, { useState } from 'react';
import './VendorsPage.css'; // Importa el archivo de estilos CSS

function VendorsPage() {
  const [vendors, setVendors] = useState([
    {
      id: 1,
      name: 'Juan',
      city: 'Quito',
      address: 'Dirección 1',
      phoneNumber: '123456789',
      whatsappNumber: '987654321',
      image: 'https://example.com/vendor1.jpg',
    },
    {
      id: 2,
      name: 'Pedro',
      city: 'Ambato',
      address: 'Dirección 2',
      phoneNumber: '123456789',
      whatsappNumber: '987654321',
      image: 'https://example.com/vendor2.jpg',
    },
    {
      id: 3,
      name: 'Carlos',
      city: 'Quito',
      address: 'Dirección 1',
      phoneNumber: '123456789',
      whatsappNumber: '987654321',
      image: 'https://example.com/vendor1.jpg',
    },
    {
      id: 4,
      name: 'Ricardo',
      city: 'Ambato',
      address: 'Dirección 2',
      phoneNumber: '123456789',
      whatsappNumber: '987654321',
      image: 'https://example.com/vendor2.jpg',
    },
    {
      id: 5,
      name: 'Juan Carlos',
      city: 'Ambato',
      address: 'Dirección 2',
      phoneNumber: '123456789',
      whatsappNumber: '987654321',
      image: 'https://example.com/vendor2.jpg',
    },

    // Agrega más vendedores según sea necesario
  ]);

  const [searchTerm, setSearchTerm] = useState('');
  const [sortOption, setSortOption] = useState('');

  const handleSearch = (event) => {
    setSearchTerm(event.target.value);
  };

  const handleSortChange = (event) => {
    setSortOption(event.target.value);
  };

  const filteredVendors = vendors.filter(
    (vendor) =>
      vendor.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
      vendor.city.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const sortedVendors = [...filteredVendors].sort((a, b) => {
    if (sortOption === 'name') {
      return a.name.localeCompare(b.name);
    } else if (sortOption === 'city') {
      return a.city.localeCompare(b.city);
    } else {
      return 0;
    }
  });

  return (
    <div className="vendors-page">
      <h2>Listado de Vendedores</h2>

      <div className="search-bar">
        <input
          type="text"
          placeholder="Buscar vendedores"
          value={searchTerm}
          onChange={handleSearch}
        />
        <select value={sortOption} onChange={handleSortChange}>
          <option value="">Ordenar por</option>
          <option value="name">Nombre</option>
          <option value="city">Ciudad</option>
        </select>
      </div>

      <div className="vendor-list">
        {sortedVendors.map((vendor) => (
          <div key={vendor.id} className="vendor-card">
            <img src={vendor.image} alt={vendor.name} className="vendor-image" />
            <h3>{vendor.name}</h3>
            <p>Ciudad: {vendor.city}</p>
            <p>Dirección: {vendor.address}</p>
            <p>Teléfono: {vendor.phoneNumber}</p>
            <button className="whatsapp-button" onClick={() => window.open(`https://wa.me/${vendor.whatsappNumber}`, '_blank')}>
              WhatsApp
            </button>
          </div>
        ))}
      </div>
    </div>
  );
}

export default VendorsPage;
